mapper.insert:
INSERT INTO tb_prk_api_info (
    api_info_sn, api_nm, api_url, api_desc, use_yn, reg_dt, rgst_id,
    rgst_ip_addr, updt_dt, updusr_id, updusr_ip_addr
) VALUES (
    #{apiInfoSn}, #{apiNm}, #{apiUrl}, #{apiDesc}, #{useYn}, #{regDt}, #{rgstId},
    #{rgstIpAddr}, #{updtDt}, #{updusrId}, #{updusrIpAddr}
)

mapper.update:
UPDATE tb_prk_api_info
api_nm = #{apiNm}, api_url = #{apiUrl}, api_desc = #{apiDesc}, use_yn = #{useYn}, reg_dt = #{regDt}, rgst_id = #{rgstId}, rgst_ip_addr = #{rgstIpAddr}, updt_dt = #{updtDt}, updusr_id = #{updusrId}, updusr_ip_addr = #{updusrIpAddr},
WHERE api_info_sn = #{apiInfoSn}
