mapper.insert:
INSERT INTO tb_co_auth_menu (
    auth_cd, menu_cd, rgst_id, reg_dt, rgst_ip_addr, updusr_id, updt_dt, updusr_ip_addr
) VALUES (
    #{authCd}, #{menuCd}, #{rgstId}, #{regDt}, #{rgstIpAddr}, #{updusrId}, #{updtDt}, #{updusrIpAddr}
)

mapper.update:
UPDATE tb_co_auth_menu
rgst_id = #{rgstId}, reg_dt = #{regDt}, rgst_ip_addr = #{rgstIpAddr}, updusr_id = #{updusrId}, updt_dt = #{updtDt}, updusr_ip_addr = #{updusrIpAddr},
WHERE auth_cd = #{authCd} AND menu_cd = #{menuCd}
