mapper.insert:
INSERT INTO tb_co_code (
    group_cd, common_cd, common_cd_nm, common_cd_desc, rgst_id, reg_dt, rgst_ip_addr,
    updusr_id, updt_dt, updusr_ip_addr, use_yn, sort_ordr
) VALUES (
    #{groupCd}, #{commonCd}, #{commonCdNm}, #{commonCdDesc}, #{rgstId}, #{regDt}, #{rgstIpAddr},
    #{updusrId}, #{updtDt}, #{updusrIpAddr}, #{useYn}, #{sortOrdr}
)

mapper.update:
UPDATE tb_co_code
common_cd_nm = #{commonCdNm}, common_cd_desc = #{commonCdDesc}, rgst_id = #{rgstId}, reg_dt = #{regDt}, rgst_ip_addr = #{rgstIpAddr}, updusr_id = #{updusrId}, updt_dt = #{updtDt}, updusr_ip_addr = #{updusrIpAddr}, use_yn = #{useYn}, sort_ordr = #{sortOrdr},
WHERE group_cd = #{groupCd} AND common_cd = #{commonCd}
