mapper.insert:
INSERT INTO tb_co_combo (
    combo_type, combo_cd, combo_nm, rgst_id, reg_dt, rgst_ip_addr,
    updusr_id, updt_dt, updusr_ip_addr
) VALUES (
    #{comboType}, #{comboCd}, #{comboNm}, #{rgstId}, #{regDt}, #{rgstIpAddr},
    #{updusrId}, #{updtDt}, #{updusrIpAddr}
)

mapper.update:
UPDATE tb_co_combo
combo_nm = #{comboNm}, rgst_id = #{rgstId}, reg_dt = #{regDt}, rgst_ip_addr = #{rgstIpAddr}, updusr_id = #{updusrId}, updt_dt = #{updtDt}, updusr_ip_addr = #{updusrIpAddr},
WHERE combo_type = #{comboType} AND combo_cd = #{comboCd}
