mapper.insert:
INSERT INTO tb_co_menu (
    menu_cd, menu_nm, upper_menu_cd, menu_url, menu_ordr, use_yn, rgst_id,
    reg_dt, rgst_ip_addr, updusr_id, updt_dt, updusr_ip_addr
) VALUES (
    #{menuCd}, #{menuNm}, #{upperMenuCd}, #{menuUrl}, #{menuOrdr}, #{useYn}, #{rgstId},
    #{regDt}, #{rgstIpAddr}, #{updusrId}, #{updtDt}, #{updusrIpAddr}
)

mapper.update:
UPDATE tb_co_menu
menu_nm = #{menuNm}, upper_menu_cd = #{upperMenuCd}, menu_url = #{menuUrl}, menu_ordr = #{menuOrdr}, use_yn = #{useYn}, rgst_id = #{rgstId}, reg_dt = #{regDt}, rgst_ip_addr = #{rgstIpAddr}, updusr_id = #{updusrId}, updt_dt = #{updtDt}, updusr_ip_addr = #{updusrIpAddr},
WHERE menu_cd = #{menuCd}
