mapper.select:
SELECT user_id, user_nm, user_pw
FROM tb_co_user
WHERE user_id = #{userId} AND user_pw = #{password}

mapper.update:
UPDATE tb_co_user
user_cd = #{userCd}, user_nm = #{userNm}, user_ty_code = #{userTyCode}, ency_de = #{encyDe}, retire_de = #{retireDe}, user_sttus_code = #{userSttusCode}, photo_unified_id = #{photoUnifiedId}, sign_unified_id = #{signUnifiedId}, dept_cd = #{deptCd}, pstn_cd = #{pstnCd}, clss_cd = #{clssCd}, rsp_cd = #{rspCd}, user_pw = #{userPw}, email = #{email}, mbtlnum = #{mbtlnum}, ownhom_telno = #{ownhomTelno}, cmpny_telno = #{cmpnyTelno}, rgsde = #{rgsde}, updde = #{updde}, user_order = #{userOrder}, duty = #{duty}, pstn_detail = #{pstnDetail}, sigungu_cd = #{sigunguCd}, area_cd = #{areaCd}, road_yn = #{roadYn}, offstreet_yn = #{offstreetYn}, attached_yn = #{attachedYn}, rgst_id = #{rgstId}, reg_dt = #{regDt}, rgst_ip_addr = #{rgstIpAddr}, updusr_id = #{updusrId}, updt_dt = #{updtDt}, updusr_ip_addr = #{updusrIpAddr}, pwd_change_dd = #{pwdChangeDd}, change_yn = #{changeYn}, cfrm_yn = #{cfrmYn},
WHERE user_id = #{userId}
