mapper.insert:
INSERT INTO tb_co_zip (
    zip, sido, sigungu, dong, li, dosari, stln, edln, zip_seq
) VALUES (
    #{zip}, #{sido}, #{sigungu}, #{dong}, #{li}, #{dosari}, #{stln}, #{edln}, #{zipSeq}
)

mapper.update:
UPDATE tb_co_zip
sido = #{sido}, sigungu = #{sigungu}, dong = #{dong}, li = #{li}, dosari = #{dosari}, stln = #{stln}, edln = #{edln},
WHERE zip = #{zip} AND zip_seq = #{zipSeq}
