mapper.select:
SELECT COALESCE(MAX(seq_no), 0)
FROM cpk.tb_attch_pic_mng_info
WHERE prk_plce_info_sn = #{prkPlceInfoSn}

    AND prk_img_id = #{prkImgId}

SELECT COALESCE(MAX(seq_no), 0)
FROM cpk.tb_attch_pic_mng_info
WHERE cmpl_sn = #{cmplSn}

    AND prk_img_id = #{prkImgId}

SELECT prk_plce_info_sn AS prkPlceInfoSn, prk_plce_type AS prkPlceType, prk_oper_type AS prkOperType, seq_no AS seqNo, ext_nm AS extNm, file_path AS filePath, file_nm AS fileNm, real_file_nm AS realFileNm, prk_img_id AS prkImgId, cmpl_sn AS cmplSn, attach_type AS attachType, reg_dt AS regDt, rgst_id AS rgstId, rgst_ip_addr AS rgstIpAddr, updt_dt AS updtDt, updusr_id AS updusrId, updusr_ip_addr AS updusrIpAddr FROM cpk.tb_attch_pic_mng_info WHERE 1=1
AND prk_plce_info_sn = #{prkPlceInfoSn}
AND prk_img_id = #{prkImgId}
AND seq_no = #{seqNo}
AND cmpl_sn = #{cmplSn}
ORDER BY seq_no ASC

mapper.update:
UPDATE cpk.tb_attch_pic_mng_info
SET
ext_nm = #{extNm},
file_path = #{filePath},
file_nm = #{fileNm},
real_file_nm = #{realFileNm},
updt_dt = NOW(),
updusr_id = #{updusrId},
updusr_ip_addr = #{updusrIpAddr}
WHERE 1=1


        AND cmpl_sn = #{cmplSn}


        AND prk_plce_info_sn = #{prkPlceInfoSn}


AND prk_img_id = #{prkImgId}
AND seq_no = #{seqNo}

mapper.delete:
DELETE FROM cpk.tb_attch_pic_mng_info
WHERE 1=1


        AND cmpl_sn = #{cmplSn}


        AND prk_plce_info_sn = #{prkPlceInfoSn}



    AND prk_img_id = #{prkImgId}


    AND seq_no = #{seqNo}
